import { Button, TextField, Typography } from '@mui/material'
import React, { useState } from 'react'
import useForm from './read';

const ToDo = () => {

  var [useritem,setuseritem]=useForm({}) 
 
  var [saveitem, setsaveitem]=useState([]);
  
  const addtoTask = () => {
    setsaveitem(
        saveitem = [...saveitem,useritem],[...saveitem,quantity]
       
        
    )
    
  }
  const myitemhandle = (event) => {
    setuseritem(
        useritem=event.target.value
    )

  }
 
  
  return (
    <div>
        <Typography variant='h4'>My Todo List</Typography>

        <TextField variant='standard' value={useritem} onChange={setuseritem}  label='Enter an Item'></TextField>
        <TextField variant='standard' value={useritem} onChange={setuseritem} label='Quantity'></TextField>
        <TextField variant='standard' value={useritem} onChange={setuseritem} label='Price'></TextField>

        <Button variant='contained' color='secondary' onClick={addtoTask} >Save</Button>

      <Typography>{useritem}</Typography>
      <ul>
     { saveitem.map((value,index)=> {
        return <div>
            <li key={index}>{value}</li>
        </div>
     })
     }
      </ul>
    </div>
  )
}

export default ToDo
